var clearLow = msg.payload[0];
var clearHigh = msg.payload[1];
var redLow = msg.payload[2];
var redHigh = msg.payload[3];
var greenLow = msg.payload[4];
var greenHigh = msg.payload[5];
var blueLow = msg.payload[6];
var blueHigh = msg.payload[7];

var clear =(clearHigh << 8) | clearLow;
var red = (redHigh << 8) | redLow;
var green = (greenHigh << 8) | greenLow;
var blue = (blueHigh << 8) | blueLow;
var r,g,b;
if(clear === 0)
{
    r = g = b = 0;
}
else
{
     r = red / clear * 255;
     g = green / clear * 255;
     b = blue / clear * 255;
}

return {payload: {r : r, g : g, b: b, a : 1}};

