var str = msg.payload;
var axes = str.slice(str.indexOf("Axes")+ 5, str.indexOf("Buttons"));
axes = axes.trimLeft();
//var axesNum = 0;
var axesValue = [];
var temp = axes;
var count = [];
var countNum = 0;
for(i = 0; i <= axes.length; i++)
{
    if(!isNaN(parseInt(axes[i])) && axes[i+1] === ' ' && axes[i+2] === ' ' && !isNaN(parseInt(axes[i+3])))
    {
        count[countNum] = i+1;
        countNum++;
    }
}
for(i = 0; i <= countNum; i++)
{
    temp = temp.slice(temp.indexOf(":") + 1);
    temp = temp.trimLeft();
    var value = temp.slice(0, temp.indexOf(" "));
    axesValue[i] = parseInt(value);
}

return {payload : {h1 : axesValue[0],v1 : axesValue[1],h2 : axesValue[2],v2: axesValue[3],h3 : axesValue[6],v3 : axesValue[7]}};

